// $color-primary: #f59a83;
$color-primary: #82c2f3;
$color-grad-1: #fb8989;
$color-grad-2: #0455bf;

$color-grad-3: rgb(18, 17, 19);
$color-grad-4: rgb(28, 9, 9);

$color-grey-light-1: #f9f5f3; // Light background
$color-grey-light-2: #f2efee; // Light lines
$color-grey-light-3: #804c80; // Light text (placeholder)
$color-grey-dark-1: #5a807e; // Normal text
$color-grey-dark-2: #5bc5a9; // Lighter text

$gradient: linear-gradient(to right bottom, $color-grad-1, $color-grad-2);
$gradient2: linear-gradient(to right bottom, $color-grad-3, $color-grad-4);

$bp-large: 78.15em; // 1250px
$bp-medium: 61.25em; // 980px
$bp-small: 37.5em; // 600px
$bp-smallest: 31.25em; // 500px

* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;

  @media only screen and (max-width: $bp-medium) {
    font-size: 50%;
  }
}

body {
  font-family: 'Nunito Sans', sans-serif;
  font-weight: 400;
  line-height: 1.6;
  color: $color-grey-dark-1;
  background-image: $gradient;
  background-size: auto;
  background-repeat: no-repeat;
  min-height: 170rem;
}

.container {
  max-width: 127rem;
  margin: 5vw auto;
  background-color: white;
  border-radius: 60px;
  display: grid;
  grid-template-rows: 0.3fr 1fr 0.5fr;
  grid-template-columns: 1fr 2fr 0.3fr;
  grid-template-areas:
    'head head head'
    'list event share'
    'footer footer footer';
  @media only screen and (max-width: $bp-large) {
    max-width: 100%;
    margin: 0;
    border-radius: 0;
  }
}

